CREATE TABLE `final-project`.`users` (`id` INT NOT NULL AUTO_INCREMENT , `name` VARCHAR(255) NOT NULL , `cnic` VARCHAR(255) NOT NULL , `nationality` VARCHAR(255) NOT NULL , `address` VARCHAR(255) NOT NULL , `mobile_phone` VARCHAR(255) NOT NULL , `email` VARCHAR(255) NOT NULL , `profile_photo` VARCHAR(255) NOT NULL , `education` VARCHAR(255) NOT NULL , `training` VARCHAR(255) NOT NULL , `working_experience` INT NOT NULL , `cv` VARCHAR(255) NOT NULL , `username` VARCHAR(255) NOT NULL , `password` VARCHAR(255) NOT NULL , `member_id` VARCHAR(255) NOT NULL , PRIMARY KEY (`id`), UNIQUE (`cnic`), UNIQUE (`email`), UNIQUE (`username`), UNIQUE (`member_id`)) ENGINE = InnoDB;
ALTER TABLE `users` CHANGE `password` `password` VARCHAR(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL;

CREATE TABLE `final-project`.`tasks` (`id` INT NOT NULL AUTO_INCREMENT , `title` VARCHAR(255) NOT NULL , `description` VARCHAR(255) NULL DEFAULT NULL , `start_date` DATE NOT NULL , `due_date` DATE NOT NULL , `priority` INT NOT NULL , `assigned_to` INT NOT NULL , `assigned_by` INT NOT NULL , `status` VARCHAR(255) NOT NULL , PRIMARY KEY (`id`)) ENGINE = InnoDB;
